log.info ("    The project property prjName= "+projectProperty);

// get a TestSuite property
def testSuiteProperty = testRunner.testCase.testSuite.getPropertyValue( "testSuiteName" );
log.info ("    The test suite property testSuiteName= "+testSuiteProperty);

// get a TestCase property
def testCaseProperty = testRunner.testCase.getPropertyValue("tcName");
log.info ("    The test case property tcName= "+testCaseProperty);

// get a TestStep property
def testStepProperty = testRunner.testCase.getTestStepByName("Properties").getPropertyValue("tsLastName");
log.info ("    The test step property tsLastName= "+testStepProperty);

// remove a global property
com.eviware.soapui.SoapUI.globalProperties.removeProperty( "gMiddleName");

// remove a project property
testRunner.testCase.testSuite.project.removeProperty("prjName") ;

// remove a testSuite property
